mapper.select:
SELECT * FROM BOARD WHERE TITLE LIKE '%' || #{searchKeyword} || '%'
AND CATEGORY = #{category}
OFFSET #{firstIndex} ROWS FETCH NEXT #{pageUnit} ROWS ONLY

mapper.insert:



	SELECT BOARD_SEQ.NEXTVAL FROM DUAL


INSERT INTO BOARD (
BOARD_ID, CATEGORY, TITLE, PREPARE, CONTENT,
THUMBNAIL, WRITE_DATE, VIEW_COUNT, WRITER_IDX
)
VALUES (
#{boardId}, #{category}, #{title}, #{prepare}, #{content},
#{thumbnail}, SYSDATE, #{viewCount}, #{writerIdx}
)

mapper.update:
UPDATE BOARD	
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE BOARD_ID = #{boardId}
