mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM API_RECIPE_KO
WHERE recipe_id = #{recipeId}

mapper.insert:
INSERT INTO API_RECIPE_KO (
    RECIPE_ID, TITLE, INSTRUCTION, INGREDIENT, CATEGORY, THUMBNAIL
)
VALUES (
    #{recipeId}, #{title}, #{instruction}, #{ingredient}, #{category}, #{thumbnail}
)
